mapper.select:
select id,account,password,real_name realName,id_card idCard,
sex,age,is_use isUse,birthday,join_time joinTime,left_time leftTime,
job_lvl jobLvl,mobile,addr from sys_user where account=#{username}

select b.role_id roleId ,b.role_code
roleCode,b.role_desc roleDesc from sys_user_role a left join sys_role
b on a.role_id=b.role_id where a.user_id=#{id}
